mapper.select:
select tp.idx idx, tp.brand_idx brand_idx, tp.brand_name brand_name, tp.product_name product_name, tp.price price ,tpp.newFileName newFileName, tpp.photo_num photo_num from product tp inner join product_photo tpp ON tp.idx = tpp.b_idx where photo_num = 1
and brand_idx = #{brand_type}
and bag_type LIKE concat('%', #{bag_name}, '%')
and product_name LIKE concat('%', #{keyword}, '%')
order by tp.idx desc limit #{displayPost}, #{postNum}
